mapper.insert:
INSERT INTO t_board
VALUES ( NULL, #{categoryId}, #{user}, SHA2(#{password}, 256), #{title}
       , #{content}, DEFAULT, #{createdTime}, #{updatedTime})

mapper.update:
UPDATE t_board
SET view_count = view_count + 1
WHERE board_id = #{boardId}

mapper.select:
SELECT count(*)
FROM t_board
WHERE 1=1

  AND created_time >= #{startDate}


  AND created_time <= #{endDate}


  AND category_id = #{category}


  AND title Like CONCAT('%',#{searchContent},'%')
  OR user Like CONCAT('%',#{searchContent},'%')
  OR content Like CONCAT('%',#{searchContent},'%')

SELECT b.*, (SELECT c.category_name FROM t_category c WHERE c.category_id = b.category_id) as category_name FROM t_board b
WHERE 1=1
AND created_time >= #{startDate}
AND created_time <= #{endDate}
AND category_id = #{category}
AND title Like CONCAT('%',#{searchContent},'%') OR user Like CONCAT('%',#{searchContent},'%') OR content Like CONCAT('%',#{searchContent},'%')
ORDER BY board_id DESC LIMIT #{pagination.rowCount} OFFSET #{pagination.offset}

SELECT IF(password = SHA2(#{password}, 256), 1, 0)
FROM t_board
WHERE board_id = #{boardId}
